-- Create 'Applicant' table
CREATE TABLE Applicant (
    Applicant_ID SERIAL PRIMARY KEY,
    ApplicantName VARCHAR(255),
    Email VARCHAR(255),
    Phone VARCHAR(50),
    Address VARCHAR(255),
    LastUpdated TIMESTAMP
);

